import randomtop_of_range = input("Type a number: ")# Input validation for the rangeif top_of_range.isdigit():    top_of_range = int(top_of_range)    if top_of_range <= 0:        print('Please type a number larger than 0 next time.')        quit()else:    print('Please type a valid number next time.')    quit()# Generate a random number within the specified rangerandom_number = random.randint(0, top_of_range)guesses = 0while True:    guesses += 1    user_guess = input("Make a guess: ")    # Input validation for the user's guess    if user_guess.isdigit():        user_guess = int(user_guess)    else:        print('Please type a valid number next time.')        continue    # Compare user's guess with the random number    if user_guess == random_number:        print("You got it!")        break    elif user_guess > random_number:        print("You were above the number!")    else:        print("You were below the number!")print(f"You got it in {guesses} guesses.")