print("Welcome to my computer quiz!")playing = input("DO you want to play? : ")if playing != "yes":    quit()print("okay! Let's play:)")score = 0answer = input("What does CPU stand for? :")if answer == "center processing unit":    print('correct!')    score += 1else:    print('Incorrect!')answer = input("What does GPU stand for? :")if answer == "graphics processing unit":    print('correct!')    score += 1else:    print('Incorrect!')answer = input("What does RAM stand for? :")if answer == "random access memory":    print('correct!')    score += 1else:    print('Incorrect!')answer = input("What does PSU stand for? :")if answer == "power supply":    print('correct!')    score += 1else:    print('Incorrect!')print("You got " + str(score) + " questions correct!")print("You got " + str((score / 4) * 100) + "%.")