import randomdef roll_dice():    return random.randint(1, 6)def player_turn(player_number):    turn_score = 0    while True:        roll = roll_dice()        if roll == 1:            print(f"You rolled a 1! Turn done!")            return 0        else:            turn_score += roll            print(f"You rolled a: {roll}")            print(f"Your score for this turn is: {turn_score}")            roll_again = input("Would you like to roll (y)? ").strip().lower()            if roll_again != 'y':                return turn_scoredef main():    num_players = int(input("Enter the number of players (2 - 4): "))    total_scores = [0] * num_players    win_score = 10  # Define the score needed to win the game    while max(total_scores) < win_score:        for i in range(num_players):            print(f"\nPlayer number {i + 1}'s turn has just started!")            print(f"Your total score is: {total_scores[i]}")            turn_score = player_turn(i + 1)            total_scores[i] += turn_score            print(f"Your total score is: {total_scores[i]}")            if total_scores[i] >= win_score:                print(f"\nPlayer {i + 1} has won the game with a score of {total_scores[i]}!\n")                break        if max(total_scores) >= win_score:            break    print("Final Scores:")    for i in range(num_players):        print(f"Player {i + 1}: {total_scores[i]}")if __name__ == "__main__":    main()