import requestsimport pyttsx3# Define the API URLAPI_URL = "https://official-joke-api.appspot.com/random_joke"def get_random_joke():    try:        # Make a GET request to the API        response = requests.get(API_URL)        # Check if the request was successful (status code 200)        response.raise_for_status()        # Parse the JSON response        joke = response.json()        # Display the joke        setup = joke['setup']        punchline = joke['punchline']        print("Here's a random joke for you:")        print(f"{setup} ... {punchline}")        # Speak the joke aloud with a female voice        speak_joke(setup, punchline)    except requests.exceptions.HTTPError as http_err:        print(f"HTTP error occurred: {http_err}")    except requests.exceptions.RequestException as req_err:        print(f"Error occurred: {req_err}")    except ValueError:        print("Response content is not in JSON format.")    except KeyError:        print("Unexpected response format.")def speak_joke(setup, punchline):    # Initialize the text-to-speech engine    engine = pyttsx3.init("sapi5")    # Set the voice to "Microsoft Zira Desktop" (female voice)    engine.setProperty('voice', 'HKEY_LOCAL_MACHINE\\SOFTWARE\\Microsoft\\Speech\\Voices\\Tokens\\TTS_MS_EN-US_ZIRA_11.0')    # Set speech rate (optional)    engine.setProperty('rate', 150)  # Speed up or slow down the speech    # Speak the setup and punchline    engine.say(setup)    engine.say("...")  # Pause before the punchline    engine.say(punchline)    # Run the speech    engine.runAndWait()# Call the function to fetch and display a random jokeget_random_joke()